mapper.update:
update x_board set
  title=#{title}
where board_id=#{no}

update x_board set
  contents=#{content}
where board_id=#{no}

update x_board set
  title=#{title},
  contents=#{content}
where board_id=#{no}

mapper.select:
select 
  board_id,
  title, 
  contents, 
  created_date,
  view_count 
from x_board


    board_id = #{no0}


    or board_id = #{no1}


    or board_id = #{no2}


    or board_id = #{no3}


    or board_id = #{no4}

select board_id, title, contents, created_date, view_count /* comments */ from x_board
board_id in /* (1, 2, 11, 12) */
#{no}
